// UTF-8 に対応した Base64 デコード関数
function base64DecodeUnicode(str) {
    return decodeURIComponent(Array.prototype.map.call(atob(str), function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));
}

// Base64 エンコードされた JavaScript
const encodedJs = "U29tZSBlbmNvZGVkIEphdmFTY3JpcHQgd2l0aCByYW1lbkRhdGE="; // ここに新しい Base64 を入れる

// Base64 をデコードして実行
const decodedJs = base64DecodeUnicode(encodedJs);
console.log("Decoded JS:", decodedJs);
eval(decodedJs);
